import React, { Component } from "react";

class UserPick extends Component {
  state = {
    usersList: ["weegembump", "happyamy2016", "jessjelly", "grumpy19"]
  };

  render() {
    <h3>
      You can select a user account to login as: this will allow you to comment
      as the user and delete your own comments{" "}
    </h3>;
    return this.state.usersList.map((user, index) => {
      return (
        <div class="card">
          <div class="card-header"></div>
          <div class="card-body">
            <h5 class="card-title">{user}</h5>
            <a href="#" class="btn btn-primary">
              {`Login as ${user}`}
            </a>
          </div>
        </div>
      );
    });
  }
}

export default UserPick;
